-        containerRef.current.style.setProperty('--card-height', `${cardHeight}px`);
-        
-        cardsRef.current.forEach((card, index) => {
-          const offsetTop = 20 + index * 20;
-          card.style.paddingTop = `${offsetTop}px`;
-        });
-      }
-    };
-
-    // Set up observers and initial state
