return (
    <section className="py-20 bg-[#f9f7f0] relative overflow-hidden">
      <div className="px-6 mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <h2 className="text-3xl md:text-[32px] font-semibold text-[#344849] mb-3">
            What Choosing Better Looks Like
          </h2>
          <p className="text-lg text-gray-600">
            Every Scoop. A Step Towards Better Living.
          </p>
        </div>

        {/* Scrollable Row */}
        <div className="flex gap-8 pb-4 overflow-x-auto scrollbar-hide">
          {testimonials.map((testimonial, index) => (
            <div
              key={index}
              className="flex flex-col gap-12 justify-between flex-shrink-0 w-[300px] p-8 bg-white rounded-2xl border border-gray-200 shadow-sm hover:shadow-md transition-shadow duration-300"
            >
              <img src={testimonial.icon} alt="Quote Icon" className="w-8 " />
              <div className="flex flex-col gap-5">
                <p className="text-lg font-medium text-[#344849]">
                  “{testimonial.text}”
                </p>
                <p className="text-gray-800 text-[16px] leading-relaxed">
                  {testimonial.des}
                </p>
              </div>
              <div className="flex items-center gap-3 mt-6">
                <img
                  src={testimonial.image}
                  alt={testimonial.author}
                  className="object-cover w-10 h-10 rounded-full"
                />
                <span className="text-sm font-medium text-gray-800">
                  {testimonial.author}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Testimonials;
